import React from 'react';
import '../App.css';
import Nav from './Nav';
import Shop from './Shop';
import About from './About';
import ItemDetail from '../ItemDetail';
import Account from '../Account/Account';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';


function App() {
  return (
    <Router>
    <div className="App">
      <Nav/>
      <Switch>
        <Route path="/" exact component={Home}/>
      <Route path="/about" component={About}/>
      <Route path="/shop" exact component={Shop}/>
      <Route path="/shop/:id/" component={ItemDetail}/>
      <Route path="/account" component={Account}/>
      </Switch>
    </div>
    </Router>
  );
}

function Home(){
  let url = "http://localhost:3000/shop";
  const linkClickImg = (url)=>{
    windown.location.href= url;
  }
  return(

  <section className="album-result-free">
    <div className="cointainer">
        <div className="row">
          <h2>Đang tìm kiếm nhạc?</h2>
          <p className="lead">Tiep tuc ban nhac da phat gan day tu doan ban dung</p>
          <a id="segmented-desktop-launch" className="btn btn-stroked-dark" role="button" href = {url} >Bat dau trinh phat tren web</a>
        </div>
        <div className="album-results">
          <ul>
            <li className="col-xs-12 col-sm-4">
              <figure onClick="">
                <div className="box">
                  <div className="content">
                  <img className="img-responsive" src="https://i.scdn.co/image/ab67706f0000000377e87398521ca9339c86cc4f" title="Hot Hits Vietnam" alt="Hot Hits Vietnam" onClick={linkClickImg('https://open.spotify.com/playlist/37i9dQZF1DX0F4i7Q9pshJ')} />
                  </div>
                </div>
              </figure>
            </li>
          </ul>
        </div>
    </div>
  </section>
  );
};
export default App;
